mapper.select:
SELECT * FROM s_course
AND name LIKE CONCAT('%', #{name}, '%') AND teacher_id LIKE CONCAT('%', #{teacherId}, '%')
LIMIT #{pagesize} OFFSET #{startIndex}

mapper.update:
UPDATE s_course
SET name = #{name}, teacher_id = #{teacherId}, course_date = #{courseDate},
    max_num = #{maxNum}, info = #{info}
WHERE id = #{id}
